import gmpy2

# Factoring challenge #1
N = gmpy2.mpz()
A = gmpy2.isqrt(N)+1
x = gmpy2.isqrt(A*A-N)
print(A-x,(A-x)*(A+x)==N)

# Factoring challenge #2
N = gmpy2.mpz()
A = gmpy2.isqrt(N)+1
while True:
    x = gmpy2.isqrt(A*A-N)
    if x*x==A*A-N:
        break
    A += 1
print(A-x,(A-x)*(A+x)==N)

# Factoring challenge #3
N = gmpy2.mpz()
A = gmpy2.isqrt(6*N) # (3p+2q-1)/2
p = (2*A+1-gmpy2.isqrt((2*A+1)**2-24*N))//6
q = (2*A+1-3*p)//2
print(p,p*q==N)

